    val listData: ArrayList<Food>
        get() {
            val list = arrayListOf<Food>()
            for (position in foodNames.indices) {
                val food = Food()
                food.name = foodNames[position]
                food.detail = foodsDetail[position]
                food.photo = foodsImages[position]
                food.titleDetail = titleFullDetail[position]
                food.full = foodsFullDetail[position]
                food.titleCompositionFood = titleFoodComposition[position]
                food.composition = compositionFood[position]

                list.add(food)
            }
            return list
        }
